mapper.update:

UPDATE BOARD_COMMENT
SET
    COMMENT_BODY = #{boardCommentBody},
    COMMENT_DATE = #{boardCommentDate}
WHERE
    BOARD_SEQ = #{id} AND COMMENT_SEQ = #{commentId}


UPDATE PRODUCT_COMMENT
SET
    COMMENT_BODY = #{productCommentBody},
    COMMENT_DATE = #{productCommentDate}
WHERE
    PRODUCT_SEQ = #{id} AND COMMENT_SEQ = #{commentId}
